mapper.insert:
INSERT INTO fe_role (ROLE_ID
                    , ROLE_NAME
                    , DESCRIPTION
                    , COLOR)
VALUES (#{roleId}
       , #{roleName}
       , #{description}
       , #{color})

mapper.update:
UPDATE FE_ROLE
SET ROLE_NAME = #{roleName},
    DESCRIPTION = #{description},
    COLOR = #{color}
WHERE ROLE_ID = #{roleId}
